import React, { useRef } from 'react';
import PropTypes from 'prop-types';
import { graphql } from 'gatsby';

import Layout from '../components/layout';
import ProjectsSlider from '../components/ProjectsSlider';
import InsightsSlider from '../components/InsightsSlider';
import CapabilitiesSlider from '../components/CapabilitiesSlider';
import LogoGrid from '../components/LogoGrid';
import SplitSection from '../components/SplitSection';
import { ContactUs, BeUs } from '../components/Prefooter';
import { colors } from '../styles';
import { useHasBeenVisible } from '../hooks/useVisibility';
import FullWidthSection from '../components/FullWidthSection';
import { NewsletterFullWidthSection } from '../components/NewsletterForm';

const Index = ({ data }) => {
  const halfPage = useRef();
  const preload = useRef();
  const hasScrolled = useHasBeenVisible(halfPage);
  const isScrolling = useHasBeenVisible(preload);

  // silly holiday treats
  // const today = new Date();
  // const isHalloween = today.getMonth() === 9 && today.getDate() === 31;
  // const isChristmas = today.getMonth() === 11 && today.getDate() === 25;

  return (
    <Layout
      headerData={{
        metaTitle: `We are an obsessive digital innovation company`,
        title: (
          <>
            {/* This is going to re-implemented after a couple weeks */}
            {/* A digital agency{' '} */}
            slaying the mundane,
            <br /> pixel by pixel.
            {/* Holy S%#*! that was fast.
            <br />
            (With{' '}
            <Link
              css={css`
                color: ${colors.darkgray};
                text-decoration: underline;
              `}
              to='/partners/gatsby/'
            >
              Gatsby
            </Link>{' '}
            it&apos;s all fire, no waiting) */}
          </>
        ),
        mobileMinHeight: '93vh',
      }}
    >
      {' '}
      <ProjectsSlider data={data.allCaseStudy} />
      <CapabilitiesSlider
        title='What We Do'
        backgroundColor={colors.lightblue}
      />
      {hasScrolled || isScrolling ? (
        <>
          <InsightsSlider data={data.allInsight} />
          <LogoGrid title='A Few of Our Friends' />
          <NewsletterFullWidthSection />
          <SplitSection>
            <ContactUs />
            <BeUs />
          </SplitSection>
        </>
      ) : (
        <FullWidthSection ref={halfPage} height='2286px' minHeight='3448px' />
      )}
    </Layout>
  );
};

Index.propTypes = {
  data: PropTypes.object.isRequired,
};

export default Index;

export const query = graphql`
  {
    allCaseStudy(
      sort: { fields: created, order: DESC }
      limit: 7
      filter: { field_hidden: { eq: false } }
    ) {
      nodes {
        ...CaseStudyFragment
      }
    }
    allInsight(
      sort: { fields: created, order: DESC }
      limit: 4
      filter: { field_hidden: { eq: false } }
    ) {
      nodes {
        ...InsightFragment
      }
    }
  }
  fragment InsightFragment on insight {
    id
    title
    field_inverse_header
    field_image {
      alt
    }
    created(formatString: "MMM D, YYYY")
    path {
      alias
    }
    relationships {
      node_type {
        name
      }
      uid {
        name: display_name
      }
      field_image {
        id
        localFile {
          publicURL
          childImageSharp {
            fluid(maxWidth: 530, maxHeight: 400) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
          childImageSlideMobile: childImageSharp {
            fluid(maxWidth: 325, maxHeight: 250) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
          childImageSlideDesktop: childImageSharp {
            fluid(maxWidth: 450, maxHeight: 400) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
        }
      }
      field_components {
        ... on component__text {
          relationships {
            component_type {
              name
            }
          }
          field_body {
            processed
          }
        }
        ... on component__image {
          id
          field_image {
            alt
          }
          relationships {
            component_type {
              name
            }
            field_image {
              id
              localFile {
                publicURL
                childImageSharp {
                  fluid(maxWidth: 630, maxHeight: 630) {
                    ...GatsbyImageSharpFluid_withWebp
                  }
                }
              }
            }
          }
        }
      }
    }
  }
  fragment CaseStudyFragment on case_study {
    id
    title
    field_subtitle
    field_inverse_header
    field_image_arrangement
    field_image {
      alt
    }
    field_secondary_image {
      alt
    }
    field_tertiary_image {
      alt
    }
    path {
      alias
    }
    relationships {
      field_tags {
        name
      }
      field_image {
        id
        localFile {
          publicURL
          childImageSharp {
            fluid(maxWidth: 850, maxHeight: 850, cropFocus: NORTH) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
          childImageMobile: childImageSharp {
            fixed(width: 335, height: 260, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeA: childImageSharp {
            fixed(width: 450, height: 320, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeB: childImageSharp {
            fixed(width: 380, height: 420, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeC: childImageSharp {
            fixed(width: 420, height: 340, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
        }
      }
      field_secondary_image {
        id
        localFile {
          publicURL
          childImageSharp {
            fluid(maxWidth: 850, maxHeight: 850) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
          childImageMobile: childImageSharp {
            fixed(width: 1, height: 1) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeA: childImageSharp {
            fixed(width: 250, height: 180, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeB: childImageSharp {
            fixed(width: 340, height: 260, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeC: childImageSharp {
            fixed(width: 270, height: 210, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
        }
      }
      field_tertiary_image {
        id
        localFile {
          publicURL
          childImageSharp {
            fluid(maxWidth: 850, maxHeight: 850) {
              ...GatsbyImageSharpFluid_withWebp
            }
          }
          childImageMobile: childImageSharp {
            fixed(width: 1, height: 1) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeA: childImageSharp {
            fixed(width: 250, height: 495, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeB: childImageSharp {
            fixed(width: 230, height: 210, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
          childImageTypeC: childImageSharp {
            fixed(width: 320, height: 210, cropFocus: CENTER) {
              ...GatsbyImageSharpFixed_withWebp_noBase64
            }
          }
        }
      }
    }
  }
`;

import React from 'react';
import { Global } from '@emotion/react';
import PropTypes from 'prop-types';

import { globalStyles } from '../styles';

import { headerPropTypes } from './Header/Header';
import Header from './Header';
import Footer from './Footer';

import '../styles/layout.css';

const Layout = ({ children, headerData }) => (
  <>
    <Global styles={globalStyles} />
    <Header {...headerData} />
    <main>{children}</main>
    <Footer />
  </>
);

Layout.propTypes = {
  children: PropTypes.node.isRequired,
  headerData: PropTypes.shape(headerPropTypes),
};

Layout.defaultProps = {
  headerData: {},
};

export default Layout;

export {
  colors,
  fonts,
  weights,
  mediaQueries,
  jsBreakpoints,
} from './css-utils';
export { globalStyles } from './globalStyles';
export {
  contValues,
  container,
  btnStyles,
  h1Xl,
  h1L,
  smSectionHead,
  contentH2,
  contentHeadings,
  dropCap,
  pLight,
  partnersProjects,
  partnersSub,
} from './custom-css';

/* Variables and functions specifically for CSS-in-JS use */

/* Media queries */
const breakpoints = [480, 767, 900, 1220];
const names = [`xs`, `tablet`, `phoneLarge`, `desktop`];

export const mediaQueries = breakpoints.reduce((acc, bp, i) => {
  acc[names[i]] = `@media (min-width: ${bp}px)`;
  return acc;
}, {});

export const jsBreakpoints = breakpoints.reduce((acc, bp, i) => {
  acc[names[i]] = bp;
  return acc;
}, {});

/* Fonts */
export const fonts = {
  serif: `'Canela', serif`,
  sans: `'NB International Pro', sans-serif`,
  mono: `'NB International Pro Mono', monospace`,
};

export const weights = {
  thin: `100`,
  light: `300`,
  regular: `400`,
  medium: `500`,
  bold: `700`,
  black: `900`,
};

/* Colors - Use a RGB to HEX converter */
export const colors = {
  white: `#fff`,
  whiteFaded: `rgba(255, 255, 255, 0.7)`,
  black: `#000`,
  gray: `#e0e0e0`,
  tagGray: `#282829`,
  lightgray: `#F8F8F9;`,
  darkgray: `#29292a`,
  darkgrayFaded: `rgba(41, 41, 42, 0.7)`,
  darkergrayFaded: `rgba(41, 41, 42, 0.9)`,
  reallydarkgray: `#282829;`,
  yellow: `#EBC900`,
  lightblue: `#a5e6ec`,
  acquiaBlue: `#0678BE`,
  lightgreen: `#cef5e2`,
  gatsbyPurple: '#663399',
  drupalBlue: '#29A8DF',
  drupal9Blue: '#009DE4',
  shopifyGreen: '#80AB42',
  bigCommerceBlue: '#0D52FF',
  red: 'red',
};

// Global CSS Styles
import { css } from '@emotion/react';

import { colors, fonts, weights, mediaQueries } from './css-utils';

// eslint-disable-next-line
export const globalStyles = css`
  /*
   * Font face declarations
   */

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-Thin'),
      url('/Fonts/Canela/Canela-Thin.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-Thin.woff') format('woff');
    font-weight: 100;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-ThinItalic'),
      url('/Fonts/Canela/Canela-ThinItalic.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-ThinItalic.woff') format('woff');
    font-weight: 100;
    font-style: italic;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-Light'),
      url('/Fonts/Canela/Canela-Light.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-Light.woff') format('woff');
    font-weight: 300;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-LightItalic'),
      url('/Fonts/Canela/Canela-LightItalic.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-LightItalic.woff') format('woff');
    font-weight: 300;
    font-style: italic;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-Regular'),
      url('/Fonts/Canela/Canela-Regular.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-Regular.woff2') format('woff2');
    font-weight: 400;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-RegularItalic'),
      url('/Fonts/Canela/Canela-RegularItalic.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-RegularItalic.woff') format('woff');
    font-weight: 400;
    font-style: italic;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-Medium'),
      url('/Fonts/Canela/Canela-Medium.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-Medium.woff') format('woff');
    font-weight: 500;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-MediumItalic'),
      url('/Fonts/Canela/Canela-MediumItalic.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-MediumItalic.woff') format('woff');
    font-weight: 500;
    font-style: italic;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-Bold'),
      url('/Fonts/Canela/Canela-Bold.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-Bold.woff') format('woff');
    font-weight: 700;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-BoldItalic'),
      url('/Fonts/Canela/Canela-BoldItalic.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-BoldItalic.woff') format('woff');
    font-weight: 700;
    font-style: italic;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-Black'),
      url('/Fonts/Canela/Canela-Black.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-Black.woff') format('woff');
    font-weight: 900;
  }

  @font-face {
    font-family: 'Canela';
    font-display: fallback;
    src: local('Canela-BlackItalic'),
      url('/Fonts/Canela/Canela-BlackItalic.woff2') format('woff2'),
      url('/Fonts/Canela/Canela-BlackItalic.woff') format('woff');
    font-weight: 900;
    font-style: italic;
  }

  @font-face {
    font-family: 'NB International Pro';
    font-display: swap;
    src: local('NBInternationalProLight'),
      url('/Fonts/NBInternationalPro/NBInternationalProLight.woff2')
        /* no woff for this font */ format('woff2');
    font-weight: 100;
  }

  @font-face {
    font-family: 'NB International Pro';
    font-display: swap;
    src: local('NBInternationalProRegular'),
      url('/Fonts/NBInternationalPro/NBInternationalProRegular.woff2')
        format('woff2'),
      url('/Fonts/NBInternationalPro/NBInternationalProRegular.woff')
        format('woff');
    font-weight: 400;
  }

  @font-face {
    font-family: 'NB International Pro';
    font-display: swap;
    src: local('NBInternationalProItalic'),
      url('/Fonts/NBInternationalPro/NBInternationalProItalic.woff2')
        format('woff2'),
      url('/Fonts/NBInternationalPro/NBInternationalProItalic.woff')
        format('woff');
    font-weight: 400;
    font-style: italic;
  }

  @font-face {
    font-family: 'NB International Pro';
    font-display: swap;
    src: local('NBInternationalProBold'),
      url('/Fonts/NBInternationalPro/NBInternationalProBold.woff2')
        format('woff2'),
      url('/Fonts/NBInternationalPro/NBInternationalProBold.woff')
        format('woff');
    font-weight: 700;
  }

  @font-face {
    font-family: 'NB International Pro';
    font-display: swap;
    src: local('NBInternationalProBoldItalic'),
      url('/Fonts/NBInternationalPro/NBInternationalProBoldItalic.woff2')
        format('woff2'),
      url('/Fonts/NBInternationalPro/NBInternationalProBoldItalic.woff')
        format('woff');
    font-weight: 700;
    font-style: italic;
  }

  @font-face {
    font-family: 'NB International Pro Mono';
    font-display: swap;
    src: local('NBInternationalProMono'),
      url('/Fonts/NBInternationalPro/NBInternationalProMono.woff2')
        format('woff2'),
      url('/Fonts/NBInternationalPro/NBInternationalProMono.woff')
        format('woff');
    font-weight: 400;
  }

  /*
   * Global Typography & Normalization
   */

  html {
    box-sizing: border-box;
    overflow-y: scroll;
    -ms-text-size-adjust: 100%;
    -webkit-text-size-adjust: 100%;
    font-size: 100%;

    ${mediaQueries.xs} {
      font-size: 112.5%;
    }
  }

  *,
  *:before,
  *:after {
    box-sizing: inherit;
  }

  body {
    margin: 0;
    font-family: ${fonts.serif};
    font-weight: ${weights.regular};
    line-height: 1.45;
    word-wrap: break-word;
    font-kerning: normal;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    -moz-font-feature-settings: 'kern', 'liga', 'clig', 'calt';
    -ms-font-feature-settings: 'kern', 'liga', 'clig', 'calt';
    -webkit-font-feature-settings: 'kern', 'liga', 'clig', 'calt';
    text-rendering: optimizeLegibility;
    font-feature-settings: 'kern', 'liga', 'clig', 'calt';
    color: ${colors.darkgray};
  }

  article,
  aside,
  details,
  figcaption,
  figure,
  footer,
  header,
  main,
  menu,
  nav,
  section,
  summary {
    display: block;
  }

  img,
  hgroup,
  dl,
  dd,
  p,
  figure,
  table,
  fieldset,
  noscript,
  iframe,
  address {
    padding: 0;
    margin: 0 0 1.45rem;
  }

  audio,
  canvas,
  progress,
  video {
    display: inline-block;
  }

  audio:not([controls]) {
    display: none;
    height: 0;
  }

  progress {
    vertical-align: baseline;
  }

  [hidden],
  template {
    display: none;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    padding: 0;
    margin: 0 0 1.45rem;
    font-family: ${fonts.serif};
    font-weight: ${weights.medium};
    line-height: 1.1;
  }

  h1 {
    font-size: 2.25rem;
  }

  h2 {
    font-size: 1.62671rem;
  }

  h3 {
    font-size: 1.38316rem;
  }

  h4 {
    font-size: 1rem;
  }

  h5 {
    font-size: 0.85028rem;
  }

  h6 {
    font-size: 0.78405rem;
  }

  p {
    margin-bottom: 27px;
    font-family: ${fonts.sans};
    font-size: 16px;
    letter-spacing: 0.2;
    line-height: 1.7;

    ${mediaQueries.phoneLarge} {
      margin-bottom: 30px;
    }
  }

  p *:last-child {
    margin-bottom: 0;
  }

  a {
    text-decoration: none;
    background-color: transparent;
    -webkit-text-decoration-skip: objects;
    color: ${colors.darkgrayFaded};
    transition: 0.3s ease all;

    &:hover,
    &:focus {
      outline-width: 0;
      color: ${colors.darkgray};
    }
  }

  b,
  strong,
  dt {
    font-weight: ${weights.bold};
  }

  abbr[title] {
    border-bottom: none;
    text-decoration: underline;
    text-decoration: underline dotted;
  }

  dfn {
    font-style: italic;
  }

  mark {
    background-color: ${colors.yellow};
    color: ${colors.darkgray};
  }

  small {
    font-size: 80%;
  }

  sub,
  sup {
    font-size: 75%;
    line-height: 0;
    position: relative;
    vertical-align: baseline;
  }

  sub {
    bottom: -0.25em;
  }

  sup {
    top: -0.5em;
  }

  code,
  kbd,
  samp {
    font-family: ${fonts.mono};
    font-weight: ${weights.regular};
    font-size: 0.85rem;
    line-height: 1.45rem;
  }

  abbr,
  acronym {
    border-bottom: 1px dotted ${colors.darkgrayFaded};
    cursor: help;
  }

  abbr[title] {
    border-bottom: 1px dotted ${colors.darkgrayFaded};
    cursor: help;
    text-decoration: none;
  }

  ul,
  ol {
    margin: 0 0 1.45rem;
    padding: 0 0 0 1rem;
    font-family: ${fonts.sans};
    font-size: 16px;
    list-style-position: outside;
    list-style-image: none;
  }

  ol li,
  ul li {
    padding-left: 0.45rem;
    margin-bottom: calc(1.45rem / 2);
  }

  li > ol {
    padding-left: 1.45rem;
    margin-bottom: calc(1.45rem / 2);
    margin-top: calc(1.45rem / 2);
  }

  li > ul {
    padding-left: 1.45rem;
    margin-bottom: calc(1.45rem / 2);
    margin-top: calc(1.45rem / 2);
  }

  li *:last-child {
    margin-bottom: 0;
  }

  li > p {
    margin-bottom: calc(1.45rem / 2);
  }

  blockquote {
    margin: 0 1.45rem 1.45rem;
    padding: 0;
  }

  blockquote *:last-child {
    margin-bottom: 0;
  }

  table {
    font-size: 1rem;
    line-height: 1.45rem;
    border-collapse: collapse;
    width: 100%;
  }

  thead {
    text-align: left;
  }

  th {
    font-weight: ${weights.bold};
  }

  td,
  th {
    text-align: left;
    border-bottom: 1px solid hsla(0, 0%, 0%, 0.12);
    font-feature-settings: 'tnum';
    -moz-font-feature-settings: 'tnum';
    -ms-font-feature-settings: 'tnum';
    -webkit-font-feature-settings: 'tnum';
    padding: 0.725rem 0.96667rem calc(0.725rem - 1px);

    &:first-of-type {
      padding-left: 0;
    }

    &:last-child {
      padding-right: 0;
    }
  }

  td *:last-child,
  th *:last-child {
    margin-bottom: 0;
  }

  pre {
    margin: 0 0 1.45rem;
    font-size: 0.85rem;
    line-height: 1.42;
    background: hsla(0, 0%, 0%, 0.04);
    border-radius: 3px;
    overflow: auto;
    word-wrap: normal;
    padding: 1.45rem;
    white-space: pre-wrap;
  }

  tt,
  code {
    background-color: hsla(0, 0%, 0%, 0.04);
    border-radius: 3px;
    font-family: ${fonts.mono};
    padding: 0;
    padding-top: 0.2em;
    padding-bottom: 0.2em;
  }

  pre code {
    background: none;
    line-height: 1.42;
  }

  code:before,
  code:after,
  tt:before,
  tt:after {
    letter-spacing: -0.2em;
    content: ' ';
  }

  hr {
    box-sizing: content-box;
    margin: 0 0 calc(1.45rem - 1px);
    background: hsla(0, 0%, 0%, 0.2);
    border: none;
    height: 1px;
  }

  /*
   * Global Image Styles
   */

  svg:not(:root) {
    overflow: hidden;
  }

  figure {
    margin: 1em 40px;
  }

  img,
  picture {
    max-width: 100%;
    border-style: none;
  }

  /*
   * Global Form Styles
   */

  button,
  input,
  optgroup,
  select,
  textarea {
    margin: 0;
    font: inherit;
  }

  optgroup {
    font-weight: ${weights.bold};
  }

  button,
  input {
    overflow: visible;
  }

  button,
  select {
    text-transform: none;
  }

  [type='reset'],
  [type='submit'],
  button,
  html [type='button'] {
    -webkit-appearance: button;
  }

  [type='button']::-moz-focus-inner,
  [type='reset']::-moz-focus-inner,
  [type='submit']::-moz-focus-inner,
  button::-moz-focus-inner {
    border-style: none;
    padding: 0;
  }

  [type='button']:-moz-focusring,
  [type='reset']:-moz-focusring,
  [type='submit']:-moz-focusring,
  button:-moz-focusring {
    outline: 1px dotted ButtonText;
  }

  fieldset {
    border: 1px solid silver;
    margin: 0 2px;
    padding: 0.35em 0.625em 0.75em;
  }

  legend {
    box-sizing: border-box;
    color: inherit;
    display: table;
    max-width: 100%;
    padding: 0;
    white-space: normal;
  }

  textarea {
    overflow: auto;
  }

  [type='checkbox'],
  [type='radio'] {
    box-sizing: border-box;
    padding: 0;
  }

  [type='number']::-webkit-inner-spin-button,
  [type='number']::-webkit-outer-spin-button {
    height: auto;
  }

  [type='search'] {
    -webkit-appearance: textfield;
    outline-offset: -2px;
  }

  [type='search']::-webkit-search-cancel-button,
  [type='search']::-webkit-search-decoration {
    -webkit-appearance: none;
  }

  ::-webkit-input-placeholder {
    color: inherit;
    opacity: 0.54;
  }

  ::-webkit-file-upload-button {
    -webkit-appearance: button;
    font: inherit;
  }
`;

