const OverlayForm = ({
  buttonText,
  confirmMessage,
  header,
  subheader,
  isActive,
  setIsActive,
  formName,
}) => {
  const [formState, updateForm] = useState({
    email: '',
    name: '',
    addressOne: '',
    addressTwo: '',
    city: '',
    state: '',
    country: '',
    zipcode: '',
    botField: '',
  });

  const [errors, updateErrors] = useState(null);
  const [hasSubmitted, setHasSubmitted] = useState(false);

  const toggle = () => {
    setIsActive(!isActive);
    setHasSubmitted(false);
  };

  const updateInput = event => {
    updateErrors(null);
    updateForm({ ...formState, [event.target.name]: event.target.value });
  };

  const onSubmit = event => {
    event.preventDefault();
    const {
      name,
      email,
      addressOne,
      city,
      state,
      country,
      zipcode,
    } = formState;
    if (hasSubmitted) {
      // Deter multiple submissions.
      updateErrors({ error: 'The form has already been submitted.' });
      return;
    }
    // Validate inputs.
    if (
      !name ||
      !email ||
      !addressOne ||
      !city ||
      !state ||
      !country ||
      !zipcode
    ) {
      // Notify user of required fields.
      const currentErrs = {};
      if (!name) {
        currentErrs.name = 'Name is required';
      }
      if (!email) {
        currentErrs.email = 'Email is required';
      }
      if (!addressOne) {
        currentErrs.addressOne = 'Address is required';
      }
      if (!city) {
        currentErrs.city = 'City is required';
      }
      if (!state) {
        currentErrs.state = 'State/Region is required';
      }
      if (!country) {
        currentErrs.country = 'Country is required';
      }
      if (!zipcode) {
        currentErrs.zipcode = 'Zipcode is required';
      }
      updateErrors(currentErrs);
      return;
    }
    // The form has not been submitted.
    fetch('/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: encode({ 'form-name': formName, ...formState }),
    }).then(() => {
      updateForm({
        email: '',
        name: '',
        addressOne: '',
        addressTwo: '',
        city: '',
        state: '',
        country: '',
        zipcode: '',
      });
      setHasSubmitted(true);
    });
  };

  const ReturnError = ({ errs, name }) => {
    return errs ? (
      <div
        css={css`
          margin-bottom: 10px;
          margin-top: -10px;
          ${mediaQueries.xs} {
            position: relative;
          }
