          align-items: center;
          background-color: ${colors.darkergrayFaded};
          z-index: 9999;
          }
        `}
      >
        <div
          css={css`
            ${mediaQueries.phoneLarge} {
              width: 700px;
              padding: 115px 24px;
            }

            @media (max-width: 900px) and (orientation: landscape) {
              padding: 4% 24px;
            }

            width: calc(100% - 60px);
            background-color: ${colors.white};
            padding: 72px 24px;
            position: relative;
            overflow-y: ${hasSubmitted ? `auto` : `scroll`};

            @media (max-width: 475px) {
              padding: 125px 24px;
            }

            ${mediaQueries.phoneLarge} {
              overflow-y: initial;
            }
          `}
        >
          <button
            type='button'
            onClick={toggle}
            data-cy='buttonFormCloseButton'
            css={css`
              ${mediaQueries.phoneLarge} {
