      font-family: ${fonts.sans};
      font-weight: ${weights.thin};
      line-height: 1;
      font-size: 16px;
    }

    div {
      max-width: 850px;
    }

    .logo-grid-container {
      flex-direction: column;
      justify-content: center;
      align-items: center;
      margin-top: 75px;

      ${mediaQueries.phoneLarge} {
        margin-top: 0;
        justify-content: space-between;
        flex-direction: row;
      }
    }

    .logo-grid-item {
      ${mediaQueries.phoneLarge} {
