      font-family: ${fonts.sans};
      font-size: 15px;
      font-weight: ${weights.light};
      line-height: 2.4;
      padding: 0 10px;
      position: relative;
    }
  `;

  return (
    <Layout
      css={layoutStyles}
      headerData={{
        metaTitle: node.header[0].title,
        title: node.header[0].title,
        subTitle: node.header[0].date,
        linksA: [
          {
            url: '../../Visit TAG at Acquia Engage 2020.ics',
            text: '+iCal',
          },
          {
            url: '../../Visit TAG at Acquia Engage 2020.ics',
            text: '+Google Calendar',
          },
