import React, { useState, useRef } from 'react';
import PropTypes from 'prop-types';
import { css } from '@emotion/react';
import ReactPlayer from 'react-player';

import useWindow from '../../hooks/useWindow';
import {
  mediaQueries,
  jsBreakpoints,
  colors,
  fonts,
  weights,
} from '../../styles';
import FullWidthSection from '../FullWidthSection';

const VideoSection = ({ url, mp4 }) => {
  const { width } = useWindow();
  const refMovie = useRef();
  const refVimeo = useRef();
  const isLgScreen = width >= jsBreakpoints.phoneLarge;
  const [playing, setPlaying] = useState(false);
  const [hasInteracted, setHasInteracted] = useState(false);
  const [buttonX, setButtonX] = useState('50%');
  const [buttonY, setButtonY] = useState('50%');
  const [buttonVisible, setButtonVisible] = useState(false);

  const sectionStyles = css`
    display: none;
    position: relative;
    padding: 0;
    background: ${colors.darkgray};

    ${mediaQueries.phoneLarge} {
      display: block;
    }

    &::before {
      content: '';
      display: block;
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      z-index: 1;
    }
  `;

  const playerStyles = css`
    position: relative;
    height: 0 !important;
    padding-top: 550px;
    overflow: hidden;

    > div {
      position: absolute;
      top: 0;

      > div {
        padding-top: 550px !important;
      }
    }

    iframe {
      width: 165.4vh !important;
      left: 50% !important;
      margin: 0 0 0 -82.7vh;
    }

    ${mediaQueries.phoneLarge} {
      padding-top: 56.25%;

      > div > div {
        padding-top: 56.25% !important;
      }

      iframe {
        width: 100% !important;
        left: 0 !important;
        margin: 0;
      }
    }
  `;

  const videoPlayer = css`
    width: 100% !important;
    height: auto !important;
    display: ${hasInteracted && playing ? 'none' : 'block'};
    opacity: ${hasInteracted && playing ? '1' : '0.65'};
  `;

  const btnStyles = css`
    display: block;
    position: relative;
    width: 80px;
    height: 80px;
    border-radius: 50%;
    border: solid 2px ${colors.white};
    background: rgba(180, 180, 180, 0.2); // @todo update this
    font-size: 0;
    color: transparent;
    cursor: pointer;

    &:focus {
      outline: none;
    }

    &::before {
      content: '';
      display: block;
      position: absolute;
      height: 0;
      width: 0;
      top: 30px;
      left: 32px;
      border: solid transparent 10px;
      border-right-width: 0;
      border-left: solid 16px ${colors.white};

      ${mediaQueries.phoneLarge} {
        display: none;
      }
    }

    ${mediaQueries.phoneLarge} {
      display: ${buttonVisible ? 'block' : 'none'};
      position: fixed;
      top: ${buttonY + 'px'};
      left: ${buttonX + 'px'};
      width: 102px;
      height: 102px;
      text-align: center;
      font-size: 15px;
      line-height: 6.8;
      font-family: ${fonts.sans};
      font-weight: ${weights.bold};
      text-transform: uppercase;
      color: ${colors.white};
      transform: translate(-50%, -50%);
      z-index: 3;
      transition: 0.2s ease-out all;
    }
  `;

  return (
    <FullWidthSection
      height='0'
      minHeight='0'
      css={sectionStyles}
      onMouseEnter={() => setButtonVisible(true)}
      onMouseLeave={() => setButtonVisible(false)}
      onMouseMove={evt => {
        setButtonX(evt.clientX);
        setButtonY(evt.clientY);
      }}
    >
      <button
        type='button'
        data-cy='vimeoButton'
        css={btnStyles}
        onClick={() => {
          if (hasInteracted) {
            setPlaying(!playing);
          } else {
            setHasInteracted(true);
            setPlaying(true);
          }
        }}
      >
        {hasInteracted && playing ? 'Pause' : 'Play'}
      </button>

      {isLgScreen && (
        <>
          <ReactPlayer
            data-cy='vimeoVideo'
            css={playerStyles}
            url={url}
            playing={playing}
            ref={refVimeo}
            volume={hasInteracted ? 1 : 0}
            width='100%'
            style={{
              display: `${hasInteracted && playing ? 'block' : 'none'}`,
              opacity: `${hasInteracted && playing ? '1' : '0.65'}`,
            }}
            config={{
              vimeo: {
                playerOptions: {
                  controls: false,
                  responsive: true,
                  autoplay: false,
                  loop: isLgScreen,
                },
              },
            }}
          />

          <video
            alt=''
            playsInline
            muted
            loop={isLgScreen}
            preload='metadata'
            autoPlay={isLgScreen}
            ref={refMovie}
            css={videoPlayer}
            data-cy='introVideo'
          >
            <source src={mp4} type='video/mp4' />
          </video>
        </>
      )}
    </FullWidthSection>
  );
};

VideoSection.propTypes = {
  mp4: PropTypes.string.isRequired,
  url: PropTypes.string.isRequired,
};

export default VideoSection;

import React from 'react';
import PropTypes from 'prop-types';
import { css } from '@emotion/react';
import { Link } from 'gatsby';

import { colors, weights, mediaQueries } from '../../styles';
import FullWidthSection from '../FullWidthSection';
import { FakeButton } from '../Button';

const CTA = ({
  backgroundColor,
  headline,
  subTitle,
  cta,
  link,
  icon,
  iconAlt,
  altStyle,
  headlineStyle,
  subTitleStyle,
  padding,
}) => {
  const ctaStyle1 = css`
    padding: ${padding || '40px 0'};

    div {
      width: 100%;
      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
    }

    h2 {
      color: ${colors.reallydarkgray};
      font-weight: ${weights.bold};
      text-align: center;
    }

    img {
      max-width: 73px;
    }
  `;

  const ctaStyle2 = css`
    padding: ${padding || '100px 48px'};

    ${mediaQueries.phoneLarge} {
      padding-left: 20px;
      padding-right: 20px;
    }

    div {
      width: 100%;
      max-width: 550px;
      display: flex;
      align-items: center;
      justify-content: center;
      flex-direction: column;
    }

    img {
      max-width: 73px;
    }

    h2 {
      color: ${colors.reallydarkgray};
      font-weight: ${weights.thin};
      font-size: 30px;
      line-height: 39px;
      text-align: center;

      ${mediaQueries.phoneLarge} {
        font-size: 36px;
        line-height: 54px;
      }
    }

    p {
      font-weight: ${weights.thin};
      line-height: 27px;
      text-align: center;
      margin-bottom: 48px;
    }
  `;

  return (
    <FullWidthSection
      backgroundColor={backgroundColor}
      minHeight='300px'
      height='300px'
      align-items='center'
      padding={padding}
      css={altStyle ? ctaStyle2 : ctaStyle1}
    >
      <div>
        {icon && <img src={icon} alt={iconAlt} />}
        {headline && <h2 css={headlineStyle}>{headline}</h2>}
        {subTitle && <p css={subTitleStyle}>{subTitle}</p>}
        {link.indexOf('#') !== -1 ? (
          <a href={link} className='anchor'>
            {' '}
            <FakeButton>{cta}</FakeButton>
          </a>
        ) : (
          <Link to={link}>
            <FakeButton>{cta}</FakeButton>
          </Link>
        )}
      </div>
    </FullWidthSection>
  );
};

CTA.propTypes = {
  backgroundColor: PropTypes.string,
  headline: PropTypes.oneOfType([PropTypes.string, PropTypes.element]),
  subTitle: PropTypes.oneOfType([PropTypes.string, PropTypes.element]),
  cta: PropTypes.string,
  link: PropTypes.string,
  icon: PropTypes.string,
  iconAlt: PropTypes.string,
  altStyle: PropTypes.bool,
  headlineStyle: PropTypes.object,
  subTitleStyle: PropTypes.object,
  padding: PropTypes.string,
};

CTA.defaultProps = {
  backgroundColor: colors.lightblue,
  headline: `Want to chat more?`,
  subTitle: '',
  cta: `GET IN TOUCH`,
  link: `/contact/`,
  icon: '',
  iconAlt: '',
  altStyle: false,
  headlineStyle: null,
  subTitleStyle: null,
  padding: '',
};

export default CTA;

