import React, { useRef } from 'react';
import PropTypes from 'prop-types';
import { css } from '@emotion/react';
import { Spring } from 'react-spring/renderprops';

import { useHasBeenVisible } from '../../hooks/useVisibility';
import { mediaQueries, weights, fonts } from '../../styles';

const CTAGridItem = ({ icon, title, description, altStyle, noPaddingImg }) => {
  const nodeRef = useRef();
  const isVisible = useHasBeenVisible(nodeRef);
  const ctaContainer = css`
    display: flex;
    flex-direction: column;
    align-items: flex-start;

    .cta-grid-item--image-wrapper {
      display: flex;
      height: 75px;
      justify-content: center;
      align-items: center;
      margin-bottom: ${noPaddingImg ? '0' : '30px'};
    }

    img {
      text-align: center;
    }

    h4 {
      font-size: 27px;
      line-height: 39px;
      ${mediaQueries.phoneLarge} {
        margin-bottom: 36px;
        font-size: 39px;
        line-height: 48px;
      }
    }

    p {
      font-weight: ${weights.thin};
    }
  `;

  const ctaContainerAlt = css`
    display: flex;
    flex-direction: row;
    align-items: center;
    padding-top: 24px;
    border-bottom: none;

    .cta-grid-item--inner-wrapper {
      display: flex;
      flex-direction: row;
      align-items: flex-start;
    }

    ${mediaQueries.phoneLarge} {
      border-bottom: 0.25px solid rgba(41, 41, 42, 0.2);
      padding: 24px 0;
    }

    img {
      margin-right: 12px;
      margin-bottom: 0;
    }

    h4 {
      font-family: ${fonts.sans};
      font-size: 16px;
      font-weight: ${weights.bold};
      margin: 0;
      line-height: 27px;
    }

    p {
      line-height: 27px;
    }

    &:nth-last-of-type(2),
    &:last-of-type {
      border-bottom: none;
    }
  `;
  return (
    <>
      {!altStyle ? (
        <div css={ctaContainer} ref={nodeRef}>
          <Spring
            delay={0.2}
            to={{
              transform: isVisible ? 'translateY(0)' : 'translateY(-25px)',
            }}
          >
            {({ transform }) => (
              <div
                style={{ transform }}
                className='cta-grid-item--image-wrapper'
              >
                {icon[0] && icon[0].node && icon[0].node.publicURL && (
                  <img src={icon[0].node.publicURL} alt={description} />
                )}
              </div>
            )}
          </Spring>

          <h4>{title}</h4>
          <p>{description}</p>
        </div>
      ) : (
        <Spring
          delay={0.2}
          to={{
            transform: isVisible ? 'translateY(0)' : 'translateY(100px)',
            opacity: isVisible ? '1' : '0',
          }}
        >
          {({ transform, opacity }) => (
            <div
              style={{ transform, opacity }}
              css={ctaContainerAlt}
              ref={nodeRef}
            >
              <div className='cta-grid-item--inner-wrapper'>
                {icon[0] && icon[0].node && icon[0].node.publicURL && (
                  <img src={icon[0].node.publicURL} alt={description} />
                )}

                <h4>{title}</h4>
                <p>{description}</p>
              </div>
            </div>
          )}
        </Spring>
      )}
    </>
  );
};

CTAGridItem.propTypes = {
  icon: PropTypes.array,
  title: PropTypes.string,
  description: PropTypes.string,
  altStyle: PropTypes.bool,
  noPaddingImg: PropTypes.bool,
};

CTAGridItem.defaultProps = {
  icon: [],
  title: '',
  description: '',
  altStyle: false,
  noPaddingImg: false,
};

export default CTAGridItem;

import React, { useRef } from 'react';
import { Link } from 'gatsby';
import PropTypes from 'prop-types';
